import React from 'react';
import { useRef } from 'react';

export default function Blog() {
    const InputRef=useRef(null)
    const rendrefocuse=()=>{
        if(InputRef.current){
            InputRef.current.focus()
            console.log()
        }
    }
    return(
        <>
        <input type="text" ref={InputRef} placeholder='entrer text' required/>
        <button onClick={rendrefocuse}>click</button>
        </>
    )
}
