import React from 'react';
import "./clothes.css"
import { Link } from 'react-router-dom';
import { menarray } from './arrayofclothes';
export const Clothes=()=>{


    return(
        <>
            <motion.div
      initial={{ opacity: 0, x: -100 }}
      animate={{ opacity: 1, x: 0 }}
      exit={{ opacity: 0, x: 100 }}
    >
        <div className='cloth-globale'>
            {menarray.map((item,index)=>{
                return(
            <div className="container" key={index}>
            
           <Link to={item.path}> <img src={item.image} alt="${index}+1"  className='image'/></Link>
         
         <Link to={item.path} className='titre'>{item.title}</Link>
         <p className="description">{item.description}</p>
         </div>
            )})}

        </div>
        </motion.div>
        
        
        
        
        
        </>
    )
}
