import React, { useMemo, useState } from 'react';
import { Link } from 'react-router-dom';
import { useRef } from 'react';
import './Blog.css'


function calculer() {
    let val=1000;
    val=val++;
    console.log('operationlourde')
    return(val);
    
}


export default function Blog() {
    const temps='hiiiiieiie';
    const [tableau,settableau]=useState([
        {id:1,nom:'foued',prenom:'trabelsi'},
        {id:1,nom:'Ali',prenom:'trabelsi'},
        {id:1,nom:'Med',prenom:'trabelsi'}
    ])
    const [valeur,setvaleur]=useState('')
    const [count,setcount]=useState(false)
    const [nombre,setnombre]=useState(0)

    const [accept,setaccept]=useState(false)
    const InputRef=useRef(null)
    const rendrefocuse=()=>{
        if(InputRef.current){
            InputRef.current.focus()
        }
    }
    const imort=useMemo(()=>calculer(),[count]);
    const submit=(e)=>{
        e.preventDefault()
        setaccept(true)
        console.log(nombre)
        setnombre(nombre+1)
            if(nombre===10){
                setcount(true)
            }
        }
    

   
       
    
    return(
        <>
        <nav>
        <div>
            <Link to='/' className="logo">Shop <span>bag</span></Link>
            <div className='searchbar'>
             <input type="text" ref={InputRef} placeholder='entrer text' required  value={valeur} onChange={(e)=>setvaleur(e.target.value)}/>
               {valeur===''&& accept &&<p className='erre'>chaine vide</p>}
                 <button onClick={submit}>click</button>
                 
                 <p>count:{nombre}</p>
                
        </div>
        
            {tableau.map((item)=>{
                return(
                    <div className="container" key={item.id} style={{
                        display:flex,
                        flexDirection:colu
                    }}>
                    <h1>le nom est {item.nom} </h1>
                    <h1>le nom est {item.prenom}</h1>
                    </div>
                )
            })}
        </div>
        
        <h1 className="red">{temps}</h1>
        
    
       
        </nav>
        </>
    )
}
