import React, { useMemo, useState } from 'react';
import { Link } from 'react-router-dom';
import { useRef } from 'react';
import './Blog.css'
import { Dropdown } from '../components/Dropdown';

function calculer(val) {
    val=val++;
    
}


export default function Blog() {
    const [valeur,setvaleur]=useState('')
    const [count,setcount]=usestate
    const [accept,setaccept]=useState(false)
    const InputRef=useRef(null)
    const rendrefocuse=()=>{
        if(InputRef.current){
            InputRef.current.focus()
        }
    }
    const submit=(e)=>{
        e.preventDefault()
        setaccept(true)
        console.log(e.target.count)
        if(e.target.count==10){
            setcount(true)
            
        }
    }
const imort=useMemo(()=>calculer(),[count]);
   
       
    
    return(
        <>
        <nav>
        <div>
            <Link to='/' className="logo">Shop <span>bag</span></Link>
            <div className='searchbar'>
             <input type="text" ref={InputRef} placeholder='entrer text' required  value={valeur} onChange={(e)=>setvaleur(e.target.value)}/>
               {valeur===''&& accept &&<p className='erre'>chaine vide</p>}
                 <button onClick={submit}>click</button>

                 <p>count:{imort}</p>
        </div>
        
        
        
    
        </div>
        </nav>
        </>
    )
}
