import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { useRef } from 'react';
import './Blog.css'
export default function Blog() {
    const [valeur,setvaleur]=useState('')
    const [accept,setaccept]=useState(false)
    const InputRef=useRef(null)
    const rendrefocuse=()=>{
        if(InputRef.current){
            InputRef.current.focus()
        }
    }
    const onMouseEnter=()=>{}
    
    return(
        <>
        <nav>
        <div>
            <Link to='/' className="logo">Shop <span>bag</span></Link>
            <div className='searchbar'>
             <input type="text" ref={InputRef} placeholder='entrer text' required  value={valeur} onChange={(e)=>setvaleur(e.target.value)}/>
               {valeur===''&& accept&&<p className='erre'>chaine vide</p>}
                 <button>click</button>
        </div>
        
        <div>
        <ul>
            <li><Link to="/" className='nav'>Home</Link></li>
            <li onMouseEnter={onMouseEnter} onMouseLeave={onMouseLeave}><Link to="/Clothing" className='nav' onClick={closeMobilemenu}>Clothing <i className='fas fa-caret-down'/>
            </Link> {dropDown &&<Dropdown />}</li>
            <li><Link to="/Electronics" className='nav' >Electronics <i className='fas fa-caret-down'/></Link>{dropDown2 &&<Dropdown2 />}</li>
            <li><Link to="/Blog" className='nav' >Blog</Link></li>
            <li><Link to="/contact us" className='nav' >Contact us</Link></li>
        </ul>
        
    </div>
        </div>
        </nav>
        </>
    )
}
