import React, { useState } from 'react'
import { Link } from 'react-router-dom'
import './Lo'

export default function Login() {
    const [password,setpassword]=useState('')
    const [accept,setaccept]=useState(false);
    const submit=(e)=>{
        e.preventDefault();
        setaccept(true);
    }
    return (
        <div>
            <div>
                <i className='' ><Link to='/Home.js' /></i>
                <button>back</button>
                <div className='formulaire'>
                    <h1> WELCOME TO SHOP<span>BAG</span></h1>
                
                    <form onSubmit={submit} method='get' action='rechercher'>
                   <input type='Email' placeholder='Entrer votre email' required></input>
                   <input type='password' placeholder='Entrer votre password' required value={password} onChange={(e)=>setpassword(e.target.value)}></input>
                   {password.length<8 && accept && <p className='erreur'>password less than 8 caractere</p>}
                    <button type='submit'>Log in</button>

                    </form>


                </div>
            </div>








        </div>
    )
}
