import React from 'react';
import { Link } from "react-router-dom";
import { useRef } from "react";
export function Navbar(){
    const inputRef=useRef(null);
    const rendrefocus=()=>{
        if(inputRef.current){
            inputRef.current.focus();
        }
    }
 return(
<>
<nav className="header">
    <div className="container1">
        <Link to="/" className="logo">EBID</Link>
        <div className="searchbar">
        <form  action="rechercher" method="get">
            <input type="text" ref={inputRef} onClick={rendrefocus} placeholder="Search"  />
          </form>
          <i className='fas fa-search'></i>
        </div>
    

   
        <ul className="list-nav">
            <li><Link to="/" className='nav' >Home</Link></li>
            <li ><Link to="/Clothes" className='nav'> 
            </Link></li>
            <li ><Link to="/Electronics" className='nav' >Explore </Link></li>
            <li><Link to="/Blog" className='nav'>Blog</Link></li>
            <li><Link to="/contact us" className='nav' >Community</Link></li>
            <li><Link to="/contact us" className='nav' >Bid</Link></li>
            <li><Link to="/contact us" className='nav' >Products</Link></li>
        </ul>
        
        <button className="button">Log in</button>

  </div>
   
        
</nav>
</>




    )
}
