import React ,{useState} from 'react';
import './Detail.css';
import { Tableimage } from './Tableimage2';
export default function Detail(){

const [ishovered,setishovered]=useState(false);

const onMouseEnter=()=>{
    setishovered(true);
}
const onMouseLeave=()=>{
    setishovered(false);
}
    return(
        <>
        <div className='Globa-detail'>
         {Tableimage.map((item,index)=>(
            <div key={index} className='image-containeur'>
            <div className='image-containeur'>
                <img src={item.image[0]}  alt={`Image ${index + 1}`}  />
                <div className="additional-image">
                {item.image.slice(1).map((image, index) => (
                <img key={index} src={image} alt={`Image ${index + 2}`} />
          ))}
        </div>
                )
            </div>
            <div>
            <h3>{item.titre}</h3>
            <p>{item.paragraphe}</p>
            <p><span>Available</span>:{item.available}</p>
            <p>{item.Brand}</p>
            <p className='price'>{item.price}</p>
            <p className='discount'>{item.discount}</p>
            <button className="button1">ADD</button>
            </div>
            </div>
            
         )
         )}          
        </div>
        
        
        
        
        
        
        
        
        </>
    )

};
