import React from 'react';
import "./Blog1.css"
import { arrayofblogs } from './arrayofblogs';
import { Link } from 'react-router-dom';
export function Blog1() {
    
    
    return(
<>
<div className='Blogglobale'>
    {arrayofblogs.map((item,index)=>{
return(
  <div className='containerblog' key={index}>
 <Link to={item.path}>
<img src={item.image} alt={`Image {index + 1}`} className='image'/>
 </Link>
 <p className='date'>{item.Date}</p>
 <Link to={item.path} className='link-blog'><p  className='title'>{item.title}</p></Link>
 <Link to={item.path} className='description'><p >{item.description}</p></Link>
</div>

)

    })}





</div>





</>



    )
}
